module.exports = function (content, file, conf) {
	// fis.log.warning(conf._views);
	//传入两个参数 URL前添加的路径(conf._views) 最终生成文件后缀(conf.temp_ext)
	var parse = '#parse',
		widget = '#widget';
	if (content.indexOf(parse) != -1 && content.indexOf(widget) != -1 ) {
		content = content.replace(/#(widget|parse)?(\(\s*['"])([^'"]+)\.vm(['"])/g, "#$1" + "$2" + conf._views + "$3." + conf.temp_ext + "$4");
	} else if (content.indexOf(parse) != -1) {
		content = content.replace(/#parse\(\s*('|")([^\(\)]+)\.vm('|")\s*\)/g, "#parse('" + conf._views + "$2." + conf.temp_ext + "')");
	} else if (content.indexOf(widget) != -1) {
		content = content.replace(/(.*#widget\s*\(\s*['"])?([^'"]+)\.vm(['"])/g, "$1" + conf._views + "$2." + conf.temp_ext + "$3");
	}
	return content;
}